<template>
  <section>
    <header-nav></header-nav>
    <router-view></router-view>
  </section>
</template>

<script>
  import Vue from 'vue'
  import VueRouter from 'vue-router'

  import HeaderNav from './components/HeaderNav'
  import HomeView from './components/Home.vue'

  // If in development, import Spectrum styles, otherwise index.html will link
  // the minified static file.
  if (process.env.NODE_ENV === 'development') {
    require('./scss/spectrum-full.scss')
  }

  Vue.use(VueRouter)

  const routes = [
    { path: '/', component: HomeView }
  ]

  export default {
    router: new VueRouter({ mode: 'history', routes }),
    components: { HeaderNav },
    beforeRouteEnter (insert) {
      const wfLoadinginterval = setInterval(() => {
        if (document.documentElement.className !== 'wf-loading') {
          clearInterval(wfLoadinginterval)
          insert()
        }
      }, 0)
    }
  }
</script>

